mapper.update:
		UPDATE TB_USER_M
		   SET USER_PW			= #{newPw}
     	  	   , CHANGE_PW_DATE	= DATE_FORMAT(NOW(), '%Y-%m-%d')
     	  	   , UPDATE_DATE	= NOW()
		 WHERE USER_ID = #{userId}

UPDATE TB_USER_M
   SET LAST_LOGIN_DATE = NOW()
 WHERE USER_ID = #{userId}

mapper.select:
SELECT USER_ID
  FROM TB_USER_M
 WHERE USER_NAME = #{userName}
   AND MOBILE = #{mobile}
 LIMIT 1

SELECT COUNT(USER_ID)
  FROM TB_USER_M
 WHERE USER_ID = #{userId}
   AND MOBILE = #{mobile}

select AUTH_ID , MENU_ID , USE_YN , ROLE_C , ROLE_R , ROLE_U , ROLE_D , ROLE_E , ROLE_P FROM TB_AUTH_MENU WHERE CONCAT(AUTH_ID, MENU_ID) IN ( SELECT DISTINCT CONCAT(AUTH_ID, MENU_ID) AS NEW_PK FROM TB_AUTH_MENU WHERE 1=1
AND AUTH_ID IN
#{auth}
)
