		, USER_NAME
		, ZIPCODE
		, ADDRESS
		, ADDRESS_DETAIL
		, EMAIL
		, MOBILE
		, SMS_YN
		, EMAIL_YN
		, CHANGE_PW_DATE
		, CREATE_USER
		, CREATE_DATE
		, UPDATE_USER
		, UPDATE_DATE
	) VALUES (
		#{userId}
		, #{userPw}
		, #{userName}
		, #{zipcode}
		, #{address}
		, #{addressDetail}
		, #{email}
		, #{mobile}
		, #{smsYn}
		, #{emailYn}
		, DATE_FORMAT(NOW(), '%Y-%m-%d')
		, #{createUser}
		, NOW()
		, #{updateUser}
		, NOW()
	)

mapper.update:
		UPDATE TB_USER_M
		   SET
		   		USER_NAME = #{userName}
				, ZIPCODE = #{zipcode}
				, ADDRESS = #{address}
				, ADDRESS_DETAIL = #{addressDetail}
				, EMAIL = #{email}
				, MOBILE = #{mobile}
				, SMS_YN = #{smsYn}
				, EMAIL_YN = #{emailYn}
				, UPDATE_USER = #{userName}
				, UPDATE_DATE = NOW()

				, USER_PW = #{newPw}
				, CHANGE_PW_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d')

      	 WHERE USER_ID 			= #{userId}

